using System;namespace ClockAdapter.ViewModels{    public interface IDigitalClock    {        void DisplayTime(string time);    }    public class AnalogClock    {        public void SetHandsRotation(int hourAngle, int minuteAngle, int secondAngle)        {            Console.WriteLine($"Analog Clock: {hourAngle}:{minuteAngle}:{secondAngle}");        }    }    public class AnalogToDigitalClockAdapter : IDigitalClock    {        private AnalogClock analogClock;        public AnalogToDigitalClockAdapter(AnalogClock analogClock)        {            this.analogClock = analogClock;        }        public void DisplayTime(string time)        {            var timeParts = time.Split(':');            if (timeParts.Length is > 3 or < 3)            {                Console.WriteLine("Неправильно введены данные.");                return;            }                        int hour = int.Parse(timeParts[0]);            int minute = int.Parse(timeParts[1]);            int second = int.Parse(timeParts[2]);            if (hour is > 23 or < 0)            {                Console.WriteLine("Неправильно введены данные.");                return;            }            if (minute is > 59 or < 0)            {                Console.WriteLine("Неправильно введены данные.");                return;            }            if (second is > 59 or < 0)            {                Console.WriteLine("Неправильно введены данные.");                return;            }                        int hourAngle = (int)(hour * 30 + minute * 0.5 + second * (1.0 / 120));            int minuteAngle = (int)(minute * 6 + second * 0.1);            int secondAngle = second * 6;                        var digitalTime = $"{hour:D2}:{minute:D2}:{second:D2}";            analogClock.SetHandsRotation(hourAngle, minuteAngle, secondAngle);                        Console.WriteLine($"Digital Clock: {digitalTime}");        }    }    public class MainWindowViewModel : ViewModelBase    {        public MainWindowViewModel()        {            AnalogClock analogClock = new AnalogClock();            IDigitalClock analogToDigitalClockAdapter = new AnalogToDigitalClockAdapter(analogClock);                        Console.Write("Введите время (в формате часов - ЧЧ:ММ:СС): ");                        var time = Console.ReadLine();            if (string.IsNullOrEmpty(time))            {                Console.WriteLine("Вы ввели пустую строку.");            }            else            {                analogToDigitalClockAdapter.DisplayTime(time);            }        }    }}