using System;namespace ClockAdapter.ViewModels{    // Интерфейс для цифровых часов    public interface IDigitalClock    {        void DisplayTime(string time);    }    // Реализация цифровых часов    public class DigitalClock : IDigitalClock    {        public void DisplayTime(string time)        {            Console.WriteLine($"Digital Clock: {time}");        }    }// Класс часов со стрелками    public class AnalogClock    {        public void SetHandsRotation(int hour, int minute, int second)        {            Console.WriteLine($"Analog Clock: {hour}:{minute}:{second}");        }    }// Адаптер для использования часов со стрелками как цифровых часов    public class AnalogToDigitalClockAdapter : IDigitalClock    {        private AnalogClock analogClock;        public AnalogToDigitalClockAdapter(AnalogClock analogClock)        {            this.analogClock = analogClock;        }        public void DisplayTime(string time)        {            // Разбираем строку времени            string[] timeParts = time.Split(':');            int hour = int.Parse(timeParts[0]);            int minute = int.Parse(timeParts[1]);            int second = int.Parse(timeParts[2]);            // Преобразуем время со стрелками в формат цифровых часов            string digitalTime = $"{hour:D2}:{minute:D2}:{second:D2}";            // Используем часы со стрелками для установки поворота стрелок            analogClock.SetHandsRotation(hour, minute, second);            // Отображаем время на цифровых часах            Console.WriteLine($"Digital Clock: {digitalTime}");        }    }    public class MainWindowViewModel : ViewModelBase    {        public MainWindowViewModel()        {            // Создаем объекты цифровых часов и часов со стрелками            IDigitalClock digitalClock = new DigitalClock();            AnalogClock analogClock = new AnalogClock();            // Создаем адаптер, передавая ему объект часов со стрелками            IDigitalClock analogToDigitalClockAdapter = new AnalogToDigitalClockAdapter(analogClock);            // Используем цифровые часы            digitalClock.DisplayTime("12:30:00");            // Используем адаптер, который позволяет использовать часы со стрелками как цифровые часы            analogToDigitalClockAdapter.DisplayTime("12:30:00");        }    }}